count(
  sum by (repo) (
    max by (repo, exported_namespace, name, container_name, silenced, current_release_name, latest_release_date, latest_release_name, current_release_date) (
      kube_inventory_pod_versions_to_latest_release{repo=~"$repo", exported_namespace=~"$namespace", name=~"$pod", container_name=~"$container"}
      )
    and on(exported_namespace, name, container_name)
    (
      (time() - kube_inventory_pod_current_release_timestamp) > ($current_release_age * 24 * 3600)
    )
    and on(exported_namespace, name, container_name)
    (
      (time() - kube_inventory_pod_latest_release_timestamp) > ($latest_release_age * 24 * 3600)
    )
  )
)

